commands.build-test-runner:
[ -e ./test.sh ] || { printf '%b' '#!/usr/bin/env bash

(
sleep() { :; };
export -f sleep;
"${BPKG_DEPS:-$(cd "$(dirname "${BASH_SOURCE[0]}")/deps" && pwd)}/tui-testkit/src/snapshot-testing.sh" "$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)" "$@"
unset -f sleep
)

exit $?
' > ./test.sh && chmod +x ./test.sh; }
